import Image from "./images/MMfox.png";
import { useMoralis } from "react-moralis";
import { useState } from "react";
const Navbar = () => {
    const handleClick = () => {
        authenticate();
        setName("Disconnect Metamask")
    }

    const [name, setName] = useState("Connect MetaMask");
    const {authenticate, isAuthenticated, logout} = useMoralis();
    if(isAuthenticated){
        setName("Disconnect MetaMask");
    }
    return ( 
        <div className="navbar">
            <h1>Giving DAO</h1>
            <div className="links">
                <a href="/" className="about">
                    About-Us
                </a>
                <a href="/" className="howItWorks">
                    How-It-Works
                </a>
                <a href="https://github.com/CharDAO" className="document">
                    Documentation
                </a>
            <button className="signin" onClick={() => {handleClick()}}>
                <img src={Image} class="center"/> {name}
            </button>
            </div>
        </div>
     );
}

export default Navbar;
